import {DIV, P} from "@step-js-html-5/index";
import {ScrollablePanel, ThemesToggler} from "@step-js-custom/index";

class GetStartedPage extends ScrollablePanel {

  constructor(...params: any) {
    super("vertical", ...params);
    this.addClassNames("flex-grow-1 p-4 position-relative");
  }

  mount() {
    let div = new DIV("d-flex justify-content-center", this);
    new DIV({
      style: {
        width: "70%",
        maxWidth: "540px",
        height: "auto"
      }
    }, div).setInnerHTML(StepJSSVG());

    div = HomeTitleWidget();
    this.add(div);

    let p = new P("ABOUT", "mt-4 text-center text-secondary", this);
    p.setStyleRule("font-size", "1.5rem");
    p.setStyleRule("font-weight", "700");

    p = new P(undefined, "mt-2 text-center", this);
    p.setInnerHTML(p0);

    p = new P(undefined, "mt-2 text-center", this);
    p.setInnerHTML(p1);

    p = new P("GET STARTED", "mt-2 text-center text-secondary", this);
    p.setStyleRule("font-size", "1.5rem");
    p.setStyleRule("font-weight", "700");

    p = new P(undefined, "mt-2 text-center", this);
    p.setInnerHTML(p2);
    p = new P("DAY / NIGHT", "mt-2 text-center text-secondary", this);
    p.setStyleRule("font-size", "1.5rem");
    p.setStyleRule("font-weight", "700");
    div = new DIV("mt-2 text-center", this);
    div.add(new ThemesToggler({
      htmlElement: document.body
    }));
  }
}

const HomeTitleWidget = () => {
  return new DIV("py-2 mx-4 d-flex flex-column align-items-center text-secondary", {
    style: {
      borderBottomColor: "var(--bs-secondary-rgb)",
      fontSize: "1.5rem",
      borderBottom: "1px solid",
      fontWeight: "700",
    }
  });
};

